"use strict";

// Object Short Syntax
var nome = 'Diego';
var idade = 23; // Parar repassar informações para dentro de um objeto
// Se a propriedade tiver o mesmo nome da variável, podemos deixar apenas 1

var usuario = {
  nome: nome,
  idade: idade,
  empresa: 'Rocketseat'
};
console.log(usuario);
